import BfaMapper from "../common/bfa_mapper.js";

/**
 * BFA mapper plugin
 * @module  plugins/mapper
 * @exports mapper
 */

"use strict";

/**
 * @classdesc
 * Plugin which extends Sandbox with BFAMapper functionality.
 * Extends Sandbox with:
 * mapper property - which contains logger instance.
 * @memberof module:plugins/mapper
 * @param {BFACore} core - bfa core instance
 * @returns {{modifySBInstance: modifySBInstance}} - object with modifySBInstance method
 * @constructor
 */
function MapperPlugin(core) {
    this._core = core;
    var _this = this;
    this._installMapper = function(target) {
        target.mapper = new BfaMapper();
    };
    return {
        modifySBInstance: function(sandboxInstance) {
            _this._installMapper(sandboxInstance);
        },
    };
}
MapperPlugin.defaultId = "mapper";
export default MapperPlugin;