import {
    Application
} from 't3js';
import {
    StoryCard,
    generateModule
} from '../story-card/story-card';

class BreakingCard extends StoryCard {
    constructor(context) {
        super(context);
        // empty behaviors so none inherited from StoryCard
        this.behaviors = [];
    }
}

BreakingCard.generateModule = generateModule;

Application.addModule('breaking-card', BreakingCard.generateModule);

export {
    BreakingCard
};