/**
 *
 * @param {Object} mapper - required - bf-analytics mapper object
 */

export function gaMap(mapper) {
    if (!mapper) {
        console.log('Cannot bind GA mapping for feed-header: No mapper defined');
    }

    const clickMapRouter = mapper.getOrCreateRouter('track/click');

    const feedHeaderBrandUrlConfig = clickMapRouter.createConfigForRoute(src => src.l === 'FeedHeader' && src.n === 'FeedHeaderBrand');
    feedHeaderBrandUrlConfig.addMapTo('action', 'click:link');
    feedHeaderBrandUrlConfig.addMapTo('category', 'feed_header');
    feedHeaderBrandUrlConfig.addMapTo('label', 'brand');

    const feedHeaderSponsorUrlConfig = clickMapRouter.createConfigForRoute(src => src.l === 'FeedHeader' && src.n === 'FeedHeaderSponsor');
    feedHeaderSponsorUrlConfig.addMapTo('action', 'click:link');
    feedHeaderSponsorUrlConfig.addMapTo('category', 'feed_header');
    feedHeaderSponsorUrlConfig.addMapTo('label', 'sponsor');
}