import dom from '../dom';
import util from '../util/standalone';

export default {
    /**
     * Appends rendered template html into module.
     * @param {Object} data - data for rendering template
     * @param {HTMLelement} data.element
     * @param {HoganTemplate} data.template
     * @param {Object} data.templateData
     * @param {Number} data.templateData.hasTextBuzzes
     * @param {Array} data.templateData.textBuzzes
     * @param {String} data.templateData.timeSince
     * @param {Object} data.translation
     * @returns {void}
     */
    renderTemplate(data) {
        let rendered = data.template.render(
            util.extend(data.templateData || {}, {
                i18n: data.translation
            })
        );
        dom.append(data.element, rendered);
        return rendered;
    }
};