import {
    Application
} from 't3js';
import {
    name as lazyImage
} from '../../js/behaviors/lazy-image';
import {
    name as viewable
} from '../../js/behaviors/viewable';
import {
    name as lazyImageBackground
} from '../../js/behaviors/lazy-image-background';
import {
    name as OneLinkPost
} from '../../js/behaviors/one-link-post';

class FeaturedCard {
    constructor() {
        this.behaviors = [
            lazyImageBackground,
            viewable,
            lazyImage,
            OneLinkPost,
        ];
    }

    init() {}
    destroy() {}
}

Application.addModule('featured-card', context => new FeaturedCard(context));

export {
    FeaturedCard
};