import {
    Application
} from 't3js';
import util from './standalone';
export * from './standalone';

/**
 * Make `util` service available in external code via `BZFD.App.getService('util')`.
 * This is used in games, @see https://buzzfeed.atlassian.net/browse/SITE-4776
 */
Application.addService('util', () => util);

export default util;